import React from 'react';
import ReactDOM from 'react-dom';
import Pagination from "react-js-pagination";


const User = (props) => (
  <tr>
    {
      <td>{props.name}</td>}
    {
      <td>{props.email}</td>
    }
    {
      <td><span className="badge badge-success">{props.status}</span></td>
    }
  </tr>
)

class Worklist extends React.Component {

  constructor(props) {
    super(props)
    this.state = {
      users: [],
      currentPage: 1,
      rows: 10
    }
  }

  handlePageChange(pageNumber) {
    console.log('active page is:' + this.currentPage)
    this.request()
  }


  componentWillMount() {
    this.request()
  }

  request() {
    fetch('https://randomuser.me/api/?page=' + this.state.page + '&results=' + this.state.rows + '&seed=abc')
      .then(response => response.json())
      .then(users => {
        users.results.forEach(user => {
          let data = {
            name: user.name.first,
            email: user.email,
            status: 'Ok',
            key: user.login.uuid
          }
          this.setState({ users: this.state.users.concat([data]) })
        })
      })
  }

  render() {
    console.log("Usuarios encontrados: " +
      this.state.users.length);
    if (this.state.users.length > 0) {
      return (
        <div>
          <table className="table table-responsive-sm table-bordered table-striped table-sm">
            <thead>
              <tr>
                <th>Name</th>
                <th>Email</th>
                <th>Status</th>
              </tr>
            </thead>
            <tbody>

              {
                this.state.users.map(user => (
                  <User key={user.key} name={user.name} email={user.email} status={user.status} />
                ))
              }

            </tbody>
          </table>

          <div>
        <Pagination
          activePage={this.state.currentPage}
          itemsCountPerPage={10}
          totalItemsCount={100}
          pageRangeDisplayed={this.state.rows}
          onChange={this.handlePageChange}
        />
      </div>

        </div>
      );
    }
    return (
      <p>Loading...</p>
    );
  }

  // const User = (props) => ()
}

// ========================================

ReactDOM.render(
  <Worklist />,
  document.getElementById('worklist')
);
